import { useState } from 'react';

import { v4 as uuidv4 } from 'uuid';

import { Button, Input, Textarea } from '../../common';
import { getCourseDuration, getCurrentDate } from '../../helpers';
import Author from './components/Author/Author';

import styles from './CreateCourse.module.css';

const CreateCourse = ({
	setCreateCourse,
	setCourses,
	existingAuthors,
	setAuthors,
}) => {
	const INITIAL_STATE = {
		id: '',
		title: '',
		description: '',
		duration: '',
		creationDate: '',
		authors: existingAuthors,
	};

	const [newCourse, setNewCourse] = useState(INITIAL_STATE);

	const { title, description, duration, authors } = newCourse;

	const resetForm = () => setNewCourse(INITIAL_STATE);

	const handleChange = (name, value) => {
		setNewCourse((prev) => ({ ...prev, [name]: value }));
	};

	const handleCourseCreate = () => {
		setCourses((prevState) => {
			const id = uuidv4();
			const creationDate = getCurrentDate();

			return [...prevState, { id, creationDate, ...newCourse }];
		});

		resetForm();
	};

	const handleAuthorCreate = () => {
		setCourses((prevState) => {
			const id = uuidv4();
			const creationDate = getCurrentDate();

			return [...prevState, { id, creationDate, ...newCourse }];
		});

		resetForm();
	};

	const handleCancelation = () => {
		setCreateCourse((prev) => !prev);
	};

	return (
		<section className={`${styles.createCourseWrapper} container`}>
			<div className={styles.row}>
				<Input
					labelText='Title'
					placeholderText='Enter title...'
					value={title}
					required={true}
					onChange={(value) => handleChange('title', value)}
				/>
				<div className={styles.buttons}>
					<Button
						title='Create Course'
						className='primary'
						onClick={handleCourseCreate}
					/>
					<Button
						title='Back to Course Listing'
						className='danger'
						onClick={handleCancelation}
					/>
				</div>
			</div>
			<div className={styles.row}>
				<Textarea
					labelText='Description'
					placeholderText='Enter description...'
					value={description}
					minLength={2}
					required={true}
					onChange={(value) => handleChange('description', value)}
				/>
			</div>

			<div className={styles.authors}>
				<div>
					<div className={`${styles.authorsBlock} ${styles.addAuthor}`}>
						<h2 className={styles.title}>Add author</h2>
						<Input
							labelText='Author name'
							placeholderText='Enter author name...'
						/>
						<Button
							title='Create author'
							className='secondary'
							onClick={handleAuthorCreate}
						/>
					</div>
					<div className={styles.authorsBlock}>
						<h2 className={styles.title}>Duration</h2>
						<Input
							labelText='Duration'
							type='number'
							placeholderText='Enter duration in minutes...'
							value={duration}
							required={true}
							onChange={(value) => {
								if (value === '0') {
									handleChange('duration', '');
								} else {
									handleChange('duration', value);
								}
							}}
						/>

						<div className={styles.duration}>
							Duration:&nbsp;{getCourseDuration(duration)}
						</div>
					</div>
				</div>
				<div>
					<div className={styles.authorsBlock}>
						<h2 className={styles.title}>Authors</h2>
						<div>
							{authors.map((author) => (
								<Author author={author} />
							))}
						</div>
					</div>
					<div className={styles.authorsBlock}>
						<h2 className={styles.title}>Course authors</h2>
						<Input
							labelText='Duration'
							placeholderText='Enter duration in minutes...'
							value={duration}
							required={true}
							onChange={(value) => handleChange('duration', value)}
						/>
					</div>
				</div>
			</div>
		</section>
	);
};

export default CreateCourse;
